// src/pages/DevoteesPage.jsx
import React, { useEffect, useState } from 'react';
import { getAllDevotees } from '../api/api';
import './DevoteesPage.css';

const PANDITS = [
  { id: 'p1', name: 'Pandit Sharma' },
  { id: 'p2', name: 'Pandit Verma' },
  { id: 'p3', name: 'Pandit Joshi' },
];

const PUJAS = [
  { id: 'puja1', name: 'Ganesh Puja' },
  { id: 'puja2', name: 'Lakshmi Puja' },
  { id: 'puja3', name: 'Saraswati Puja' },
];

function DevoteesPage() {
  const [devotees, setDevotees] = useState([]);
  const [editId, setEditId] = useState(null);
  const [form, setForm] = useState({ name: '', email: '' });

  // Booking form state
  const [booking, setBooking] = useState({
    panditId: '',
    pujaId: '',
    devoteeName: '',
    devoteeEmail: '',
  });

  const [bookingStatus, setBookingStatus] = useState(null); // success or error message
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchDevotees();
  }, []);

  async function fetchDevotees() {
    try {
      const res = await getAllDevotees();
      setDevotees(res.data);
    } catch (e) {
      console.error('Failed to fetch devotees', e);
    }
  }

  function handleEdit(d) {
    setEditId(d._id);
    setForm({ name: d.name, email: d.email });
  }

  async function handleSave() {
    try {
      await fetch(`https://localhost:5000/api/users/update/${editId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(form),
      });
      setEditId(null);
      fetchDevotees();
    } catch (e) {
      console.error('Failed to save devotee', e);
    }
  }

  // Handle booking input change
  function handleBookingChange(e) {
    const { name, value } = e.target;
    setBooking(b => ({ ...b, [name]: value }));
  }

  async function handleBookingSubmit(e) {
    e.preventDefault();
    setBookingStatus(null);
    if (!booking.panditId || !booking.pujaId || !booking.devoteeName || !booking.devoteeEmail) {
      setBookingStatus({ error: 'Please fill in all booking fields.' });
      return;
    }
    setLoading(true);
    try {
      // Mock API call delay - replace with actual booking API endpoint
      await new Promise(res => setTimeout(res, 1000));

      // Example API call (uncomment and modify as needed):
      /*
      await fetch('https://localhost:5000/api/pujas/book', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(booking),
      });
      */

      setBookingStatus({ success: 'Puja booked successfully!' });

      setBooking({
        panditId: '',
        pujaId: '',
        devoteeName: '',
        devoteeEmail: '',
      });
    } catch (error) {
      setBookingStatus({ error: 'Failed to book puja. Please try again.' });
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="devotees-page">
      <h2>Book New Puja</h2>

      <form className="booking-form" onSubmit={handleBookingSubmit}>
        <label>
          Select Pandit:
          <select name="panditId" value={booking.panditId} onChange={handleBookingChange}>
            <option value="">-- Select Pandit --</option>
            {PANDITS.map(p => (
              <option key={p.id} value={p.id}>
                {p.name}
              </option>
            ))}
          </select>
        </label>

        <label>
          Select Puja:
          <select name="pujaId" value={booking.pujaId} onChange={handleBookingChange}>
            <option value="">-- Select Puja --</option>
            {PUJAS.map(p => (
              <option key={p.id} value={p.id}>
                {p.name}
              </option>
            ))}
          </select>
        </label>

        <label>
          Devotee Name:
          <input
            type="text"
            name="devoteeName"
            value={booking.devoteeName}
            onChange={handleBookingChange}
            placeholder="Enter devotee's name"
            required
          />
        </label>

        <label>
          Devotee Email:
          <input
            type="email"
            name="devoteeEmail"
            value={booking.devoteeEmail}
            onChange={handleBookingChange}
            placeholder="Enter devotee's email"
            required
          />
        </label>

        <button type="submit" disabled={loading}>
          {loading ? 'Booking...' : 'Book Puja'}
        </button>
      </form>

      {bookingStatus?.error && <p className="error-msg">{bookingStatus.error}</p>}
      {bookingStatus?.success && <p className="success-msg">{bookingStatus.success}</p>}

      <hr style={{ margin: '2rem 0' }} />

      <h2>Devotees List</h2>
      {devotees.length === 0 ? (
        <p>No devotees found.</p>
      ) : (
        <div className="devotees-list">
          {devotees.map(d => (
            <div key={d._id} className="devotee-card">
              {editId === d._id ? (
                <>
                  <input
                    value={form.name}
                    onChange={e => setForm(f => ({ ...f, name: e.target.value }))}
                    placeholder="Name"
                  />
                  <input
                    value={form.email}
                    onChange={e => setForm(f => ({ ...f, email: e.target.value }))}
                    placeholder="Email"
                  />
                  <button onClick={handleSave}>Save</button>
                  <button onClick={() => setEditId(null)}>Cancel</button>
                </>
              ) : (
                <>
                  <p>
                    <strong>{d.name}</strong>
                  </p>
                  <p>{d.email}</p>
                  <button className="edit-btn" onClick={() => handleEdit(d)}>
                    Edit
                  </button>
                </>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default DevoteesPage;
