import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { motion, AnimatePresence } from "framer-motion";
import { Search } from 'lucide-react'; // Import the Search icon from Lucide React
import "./Home.css";
import { postLocation } from '../api/api';

const backendURL = "http://localhost:5000";

function getPoojaImage(img) {
  if (!img) return "/images/default-pooja.png";
  if (img.startsWith("http://") || img.startsWith("https://")) return img;
  return `${backendURL}${img}`;
}

const Home = () => {
  const [pandits, setPandits] = useState([]);
  const [poojas, setPoojas] = useState([]);
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedService, setSelectedService] = useState(null);
  const [showChatbot, setShowChatbot] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [isHoveringSearch, setIsHoveringSearch] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [panditRes, poojaRes, serviceRes] = await Promise.all([
          axios.get(`${backendURL}/api/pandits/view`),
          axios.get(`${backendURL}/api/poojas/view`),
          axios.get(`${backendURL}/api/services/view`),
        ]);
        setPandits((panditRes.data || []).filter((p) => p.is_verified));
        setPoojas(poojaRes.data || []);
        setServices(serviceRes.data || []);
      } catch {
        setPandits([]);
        setPoojas([]);
        setServices([]);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  // Location fetching effect (no UI, only post)
  useEffect(() => {
    if (!("geolocation" in navigator)) return;
    const watchId = navigator.geolocation.watchPosition(
      (pos) => {
        postLocation({
          latitude: pos.coords.latitude,
          longitude: pos.coords.longitude
        }).catch(() => {});
      },
      (err) => {},
      { enableHighAccuracy: true, maximumAge: 0 }
    );
    return () => navigator.geolocation.clearWatch(watchId);
  }, []);

  // Filtered poojas based on search query
  const filteredPoojas = poojas.filter((pooja) =>
    pooja.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="home-container">
      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-left">
          <motion.img src="/images/subh.png" alt="Logo" className="logo-circle"
            initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ duration: 0.5 }}
          />
          <span className="logo-text">Shubhkarya</span>
        </div>
        <div className="navbar-right">
          <a href="#about">About Us</a>
          <a href="#services">Services</a>
          <a href="#poojas">Pooja Provided</a>
          <a href="#pandits">Pandits</a>
          <a href="#new-footer">Contact</a>
          <Link to="/login" className="login-box">Login</Link>
        </div>
      </nav>

      {/* Hero */}
      <header className="hero" style={{ backgroundImage: `url(/images/ho1.png)` }}>
        <div className="hero-overlay">
          <motion.div
            className="hero-content"
            initial={{ opacity: 0, y: 50 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.7 }}
          >
            <div className="hero-left">
              <h1 className="main-heading">
                Shubhkarya:<br />
                Your Trusted Online<br />
                Pandit Booking
              </h1>
              <p className="tagline">Your Spiritual Partner: For Every Sacred Occasion</p>
              <div className="hero-buttons">
                <button className="book-btn" onClick={() => navigate('/login')}>Book Now</button>
                <button className="get-started-btn" onClick={() => navigate('/signup')}>Get Started</button>
              </div>
            </div>
          </motion.div>
        </div>
      </header>

      {/* About */}
      <section className="about-section" id="about">
        <motion.h2 className="section-title"
          initial={{ x: -150, opacity: 0 }} whileInView={{ x: 0, opacity: 1 }}
          transition={{ duration: 0.7 }}
        >About Shubkarya</motion.h2>
        <div className="about-content">
          <motion.div className="about-column" initial={{ y: 60, opacity: 0 }} whileInView={{ y: 0, opacity: 1 }}>
            <div className="feature-row">
              <img src="/images/i1.jpeg" alt="Vedic Poojas Icon" />
              <div>
                <h3>Vedic Poojas</h3>
                <p>Performed by experienced Pandits with authentic rituals.</p>
              </div>
            </div>
            <div className="feature-row">
              <img src="/images/i2.jpeg" alt="Easy Booking Icon" />
              <div>
                <h3>Easy Booking</h3>
                <p>Book poojas anytime with a few simple clicks.</p>
              </div>
            </div>
            <div className="feature-row">
              <img src="/images/i4.jpeg" alt="Verified Pandits Icon" />
              <div>
                <h3>Verified Pandits</h3>
                <p>Only trusted and verified professionals available.</p>
              </div>
            </div>
          </motion.div>
          <div className="logo-center">
            <img src="/images/subh.png" alt="Shubkarya Logo" />
          </div>
          <motion.div className="about-column" initial={{ y: 60, opacity: 0 }} whileInView={{ y: 0, opacity: 1 }}>
            <div className="feature-row">
              <img src="/images/i6.jpeg" alt="Traditional Rituals Icon" />
              <div>
                <h3>Traditional Rituals</h3>
                <p>Rooted in ancient Vedic traditions and customs.</p>
              </div>
            </div>
            <div className="feature-row">
              <img src="/images/i5.jpeg" alt="Multiple Services Icon" />
              <div>
                <h3>Multiple Services</h3>
                <p>From Griha Pravesh to Wedding, all covered.</p>
              </div>
            </div>
            <div className="feature-row">
              <img src="/images/i3.jpeg" alt="24x7 Support Icon" />
              <div>
                <h3>24x7 Support</h3>
                <p>Were here to help you anytime, anywhere.</p>
              </div>
            </div>
          </motion.div>
        </div>
      </section>

      {/* OUR SERVICES */}
      <section className="services-container" id="services">
        <motion.h2
          initial={{ scale: 0.7, opacity: 0 }} whileInView={{ scale: 1, opacity: 1 }}
          transition={{ duration: 0.5 }}
        >OUR SERVICES</motion.h2>
        <p style={{ textAlign: "center" }}>Discover a wide range of spiritual services tailored to your needs.</p>
        <div className="services-grid">
          {services.map((service, i) => (
            <motion.div
              key={service._id}
              className="service-card"
              onClick={() => navigate('/login')}
              whileHover={{ scale: 1.07, boxShadow: "0 8px 28px #ffc10744" }}
              initial={{ opacity: 0, y: 50 }} animate={{ opacity: 1, y: 0 }}
              transition={{ delay: i * 0.08, duration: 0.35 }}
            >
              <img src={service.image} alt={service.name} className="service-image" />
              <h3>{service.name}</h3>
              <p>{service.description}</p>
              <button className="book-now">Book Now</button>
            </motion.div>
          ))}
        </div>
        {/* Updated service-details section to match the image */}
        <motion.div className="service-details-grid"
          initial={{ y: 60, opacity: 0 }} whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.7 }}
        >
            <div className="detail-item">
                <img src="/images/s1.png" alt="Spiritual Guide Icon" className="detail-icon" />
                <h4>4000+ Spiritual Guide</h4>
                <p>Priests, Pandits, Religious Experts & Consultants</p>
            </div>
            <div className="detail-item">
                <img src="/images/s2.png" alt="Pooja Types Icon" className="detail-icon" />
                <h4>500+ Types of Puja</h4>
                <p>500+ Types of Religious Services</p>
            </div>
            <div className="detail-item">
                <img src="/images/s3.png" alt="Pooja Performed Icon" className="detail-icon" />
                <h4>100000+ Puja Performed</h4>
                <p>4000+ Spiritual Guides performed more than 100000+ Puja</p>
            </div>
        </motion.div>
      </section>

      {/* Pooja Provided */}
      <section id="poojas" className="poojas">
        {/* Heading and Search Icon/Bar Wrapper */}
        <div className="poojas-header-wrapper">
          <h2>Pooja Provided</h2>
          <motion.div
            className="pooja-search-wrapper"
            onMouseEnter={() => setIsHoveringSearch(true)}
            onMouseLeave={() => setIsHoveringSearch(false)}
            layout // Enables automatic layout animations for the wrapper
            transition={{ duration: 0.4, type: "spring", damping: 20, stiffness: 200 }}
          >
            {/* Search Icon (Lucide React) */}
            <motion.div
              className="search-pooja-icon"
              initial={false}
              animate={{
                scale: isHoveringSearch ? 1.15 : 1,
                rotate: isHoveringSearch ? 20 : 0,
                backgroundColor: isHoveringSearch ? 'rgba(255, 197, 85, 0.4)' : 'rgba(255, 197, 85, 0.2)',
                boxShadow: isHoveringSearch ? '0 8px 25px rgba(214, 127, 42, 0.4)' : '0 2px 10px rgba(214, 127, 42, 0.2)',
              }}
              transition={{ duration: 0.3 }}
            >
              <Search size={28} /> {/* Lucide Search icon */}
            </motion.div>

            {/* Search Bar - Conditionally rendered with AnimatePresence */}
            <AnimatePresence>
              {isHoveringSearch && (
                <motion.div
                  className="pooja-search-bar"
                  initial={{ width: 0, opacity: 0, x: -20, padding: '0 5px' }}
                  animate={{ width: '400px', opacity: 1, x: 0, padding: '0 15px' }}
                  exit={{ width: 0, opacity: 0, x: -20, padding: '0 5px' }}
                  transition={{ duration: 0.3, ease: "easeOut" }}
                >
                  <input
                    type="text"
                    placeholder="Search for a Pooja..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="pooja-search-input"
                    autoFocus
                  />
                  <Search size={24} className="search-input-icon" /> {/* Lucide Search icon */}
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>
        </div>

        {loading ? (
          <p>Loading poojas...</p>
        ) : (
          <motion.div className="card-grid" initial="hidden" animate="visible" variants={{
            visible: { transition: { staggerChildren: 0.08 } }
          }}>
            {filteredPoojas.length > 0 ? (
              filteredPoojas.map((pooja, i) => (
                <motion.div
                  className="service-card"
                  key={pooja._id}
                  onClick={() => setSelectedService(pooja)}
                  whileHover={{ scale: 1.07, boxShadow: "0 8px 32px #ffa72640" }}
                  initial={{ opacity: 0, y: 30 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: i * 0.06 + 0.2, duration: 0.32 }}
                >
                  <img
                    src={getPoojaImage(pooja.imageUrl)}
                    alt={pooja.name}
                    className="service-img"
                  />
                  <h3>{pooja.name}</h3>
                </motion.div>
              ))
            ) : (
              <p className="no-poojas-found">No Poojas found matching your search.</p>
            )}
          </motion.div>
        )}
        <AnimatePresence>
          {selectedService && (
            <motion.div className="service-description-overlay"
              onClick={() => setSelectedService(null)}
              initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}
            >
              <motion.div
                className="service-description-modal"
                onClick={e => e.stopPropagation()}
                initial={{ scale: 0.7, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.7, opacity: 0 }}
                transition={{ duration: 0.25 }}
              >
                <h3>{selectedService.name}</h3>
                <img
                  src={getPoojaImage(selectedService.imageUrl)}
                  alt={selectedService.name}
                  style={{ width: "100%", marginBottom: 12, borderRadius: 6 }}
                />
                <p>{selectedService.description}</p>
                <button onClick={() => setSelectedService(null)} className="close-btn">Close</button>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </section>

      {/* Pandits */}
      <section id="pandits" className="pandits">
        <h2>Our Verified Pandits</h2>
        <motion.div className="card-section"
          initial={{ opacity: 0 }} whileInView={{ opacity: 1 }}
          transition={{ duration: 0.5, delay: 0.3 }}
        >
          {pandits.map((pandit, i) => (
            <motion.div
              className="pandit-card"
              key={pandit._id}
              initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }}
              transition={{ delay: i * 0.06, duration: 0.29 }}
            >
              <img
                src={
                  pandit.profile_photo_url &&
                  pandit.profile_photo_url.startsWith("/uploads")
                    ? `${backendURL}${pandit.profile_photo_url}`
                    : pandit.profile_photo_url
                }
                alt={pandit.name}
              />
              <h3>{pandit.name}</h3>
              <p>{pandit.experience} years experience</p>
              <p>{pandit.language}</p>
            </motion.div>
          ))}
        </motion.div>
      </section>

      {/* New Enhanced Footer */}
      <footer className="footer" id="new-footer">
        <div className="footer-content">
          {/* Company Info Section */}
          <div className="footer-section footer-about">
            <div className="footer-logo-area">
              <img src="/images/subh.png" alt="Shubhkarya Logo" className="footer-logo" />
              <span className="footer-company-name">Shubhkarya</span>
            </div>
            <p className="footer-description">
              Shubhkarya is your trusted online platform for booking experienced Pandits and
              performing authentic Vedic Poojas. We ensure a seamless spiritual experience
              for every sacred occasion, bringing ancient traditions to your doorstep.
            </p>
            <p className="footer-copyright">&copy; {new Date().getFullYear()} Shubhkarya. All rights reserved.</p>
          </div>

          {/* Quick Links Section (Content you want to add) */}
          <div className="footer-section footer-links">
            <h3>Quick Links</h3>
            <ul>
              <li><a href="#about">About Us</a></li>
              <li><a href="#services">Our Services</a></li>
              <li><a href="#poojas">Pooja Types</a></li>
              <li><a href="#pandits">Our Pandits</a></li>
              <li><Link to="/privacy-policy">Privacy Policy</Link></li> {/* Opens new page */}
              <li><Link to="/terms-conditions">Terms & Conditions</Link></li> {/* Opens new page */}
            </ul>
          </div>

          {/* Contact Info Section */}
          <div className="footer-section footer-contact">
            <h3>Contact Us</h3>
            <p><i className="fas fa-phone-alt"></i> Mobile: +91 98765 43210</p>
            <p><i className="fas fa-envelope"></i> Email: info@shubhkarya.com</p>
            <p><i className="fas fa-map-marker-alt"></i> Address: 123 Divine Path, Spiritual Nagar, Hathras, Uttar Pradesh, India - 204101</p>
          </div>
        </div>
      </footer>

      {/* Chatbot Icon */}
      <motion.button
        className="chatbot-toggle-button"
        onClick={() => setShowChatbot(!showChatbot)}
        initial={{ opacity: 0, scale: 0 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 1 }}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        aria-label="Toggle Chatbot"
      >
        <img src="/images/subh.png" alt="Chatbot Icon" className="chatbot-icon" />
      </motion.button>

      {/* Chatbot Popup */}
      <AnimatePresence>
        {showChatbot && (
          <motion.div
            className="chatbot-popup"
            role="dialog"
            aria-modal="true"
            aria-label="Chatbot window"
            initial={{ opacity: 0, y: 50, x: 50 }}
            animate={{ opacity: 1, y: 0, x: 0 }}
            exit={{ opacity: 0, y: 50, x: 50 }}
            transition={{ duration: 0.3 }}
          >
            <iframe
              title="Chatbot"
              src="https://www.chatbase.co/chatbot-iframe/usovl2iS71gPfrO5xmRyP"
              style={{ width: "100%", height: "100%", border: "none", borderRadius: 15 }}
              allow="clipboard-write"
            />
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default Home;
